import React, { Component } from 'react';
import axios from 'axios'
import './App.css';
import Post from './post.js'

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      posts: [],
      login: [],
    }
  }

  componentDidMount() {
        axios.get('http://localhost:5000').then(res => {
                console.log(` THIS IS THE RESULT   ${res}`)
                this.setState({
                    posts: res.data
                })
          })
          .catch(error => console.log(error))

          axios.get('http://localhost:5000/login').then(res => {
            this.setState({login: res})
          })

      }



  render(){

    return(

      <div>

        <h1>Login</h1>
        {
          (this.state.login).map(form => (
            JSON.stringify(form)
          ))
        }

        <form>
          <label>Username</label><input type="text" id="email" name="email" />
          <label>Password</label><input type="text" id="password" name="password" />
        </form>



        <h1>sign up</h1>


          <h1>Your Recent </h1>
        {
          (this.state.posts).map(post => (
            <Post post={post.title} />
          ))
        }

      </div>
    );
  }
}

export default App;
