import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { Button, Form } from 'react-bootstrap'; //remember to import CSS styles CDN from React-bootstrap
import Home from './components/Home'  //exact path because of default???
import CustomNav from './components/CustomNav'
import Footer from './components/Footer'
import axios from 'axios'

import Post from './components/Post'
import Posts from './components/Posts'


import './App.css';

class App extends Component {

  // show modals on click and hide opon click outside of the div
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      showModal:''
    }
    this.handleModal = this.handleModal.bind(this);
    this.handleOutsideClick = this.handleOutsideClick.bind(this);
  }
  handleModal = e => {
    this.setState({
        showModal: e.target.id,
    });
  }
  closeModal = () => {
      this.setState({
          showModal:''
      });
  }
  handleOutsideClick = e => {
    if (this.modal && this.modal.contains(e.target)) return;
     this.closeModal()
  }


  componentDidMount() {
    axios.get('http://localhost:5000').then(res => {
            console.log(` THIS IS THE RESULT   ${res}`)
            this.setState({
                posts: res.data
            })
       })
       .catch(error => console.log(error))
    }

  render() {
    {console.log("hhheeeeeeeeeeyyyyyyyyyyyyy thereeeeeeeeeeeeee")}

    return (

        <div>
                        {Object.keys(this.state.posts).map(post => { return post})}

          </div>
    );
  }
}

export default App;
